-- create three new tables for car registration plate sensors observations, Bluetooth and wifi device sensors observations, people counter (number of faces) sensors observations
create table obsv_carregplate_detection (carRegPlate_num varchar(255), created datetime not null, hex_carRegPlate_num BIGINT, latitude float, longitude float, obsv_time datetime, updated datetime not null, version integer, carRegPlate_obsv_id bigint not null, primary key (carRegPlate_obsv_id), unique (carRegPlate_obsv_id)) ENGINE=InnoDB;
create table obsv_device_detection (created datetime not null, hex_macAddress bigint, latitude float, longitude float, macAddress varchar(50), obsv_time datetime, signalStrength integer, updated datetime not null, version integer, devDetect_obsv_id bigint not null, primary key (devDetect_obsv_id), unique (devDetect_obsv_id)) ENGINE=InnoDB;
create table obsv_people_counter (created datetime not null, latitude float, longitude float, number integer, obsv_time datetime, updated datetime not null, version integer, pCount_obsv_id bigint not null, primary key (pCount_obsv_id), unique (pCount_obsv_id)) ENGINE=InnoDB;
alter table obsv_carregplate_detection add index FKB05A8853FF03C62B (carRegPlate_obsv_id), add constraint FKB05A8853FF03C62B foreign key (carRegPlate_obsv_id) references observations (obsv_id);
alter table obsv_device_detection add index FK80B2798537C0404D (devDetect_obsv_id), add constraint FK80B2798537C0404D foreign key (devDetect_obsv_id) references observations (obsv_id);
alter table obsv_people_counter add index FKB3219035AC1038F4 (pCount_obsv_id), add constraint FKB3219035AC1038F4 foreign key (pCount_obsv_id) references observations (obsv_id);